mapper.select:
SELECT id, title, person_id, topic, img, content, create_time, comment_num FROM t_article where 1=1
AND topic like CONCAT('%', #{topic}, '%')
AND person_id=#{personId}
AND id=#{id}
order by create_time DESC

SELECT count(1) FROM t_article where 1=1
AND topic like CONCAT('%', #{topic}, '%')
AND person_id=#{personId}

mapper.insert:
    insert into t_article (
	title,
	person_id,
	topic,
	img,
	content,
	create_time
	)values(
	#{title},#{personId},#{topic},#{img},#{content},#{createTime}
	)
